mapper.select:
SELECT product_id AS productId, user_id AS userId, title, price, region, like_count AS likeCount, chat_count AS chatCount, view_count AS viewCount, status FROM products WHERE product_id IN
#{productId}
(NULL)

SELECT product_id AS productId, user_id AS userId, title, price, region, like_count AS likeCount, chat_count AS chatCount, view_count AS viewCount, status, hidden FROM products WHERE user_id = #{userId} AND hidden = 0
AND status IN (#{status}, 'Booking')
AND status = #{status}

mapper.delete:
DELETE
FROM product_images
WHERE image_id NOT IN 

	#{image}

AND product_id = #{productId}

mapper.update:
UPDATE products
SET status = #{status}
WHERE product_id = #{productId}

UPDATE products
SET hidden = #{hidden}
WHERE product_id = #{productId}

mapper.select:
SELECT image_file_name FROM product_images
WHERE product_id = #{value}
ORDER BY image_id


SELECT COUNT(*)
FROM LIKES
WHERE user_id = #{userId} and product_id = #{productId}

mapper.update:
UPDATE products
SET LIKE_COUNT = LIKE_COUNT + 1
WHERE PRODUCT_ID = #{value}

UPDATE products
SET LIKE_COUNT = LIKE_COUNT - 1
WHERE PRODUCT_ID = #{value}

UPDATE PRODUCTS
SET CHAT_COUNT = CHAT_COUNT + 1
WHERE PRODUCT_ID = #{value}

UPDATE PRODUCTS
SET CHAT_COUNT = CHAT_COUNT - 1
WHERE PRODUCT_ID = #{value}

UPDATE products
SET VIEW_COUNT = VIEW_COUNT + 1
WHERE PRODUCT_ID = #{value}
