mapper.select:
SELECT product_id AS productId, user_id AS userId, title, price, region, like_count AS likeCount, chat_count AS chatCount, view_count AS viewCount, status FROM products WHERE product_id IN
#{productId}
(NULL)

SELECT product_id AS productId, user_id AS userId, title, price, region, like_count AS likeCount, chat_count AS chatCount, view_count AS viewCount, status FROM products WHERE user_id = #{userId}
AND status IN (#{status}, 'Booking')
AND status = #{status}

mapper.update:
UPDATE products
SET status = #{status}
WHERE product_id = #{productId}

mapper.select:

SELECT COUNT(*)
FROM LIKES
WHERE user_id = #{userId} and product_id = #{productId}

mapper.update:
UPDATE products
SET LIKE_COUNT = LIKE_COUNT + 1
WHERE PRODUCT_ID = #{value}

UPDATE products
SET LIKE_COUNT = LIKE_COUNT - 1
WHERE PRODUCT_ID = #{value}

UPDATE products
SET VIEW_COUNT = VIEW_COUNT + 1
WHERE PRODUCT_ID = #{value}
